import { useReducer} from "react";
import Questions from "./Question";

const initialState = {                              //object
    currentQuetionIndex: 0,
    questions: [],
};

const reducer = (state, action) => {
    if (action.type === "Next_QUESTION"){
    return {...state, currentQuetionIndex: state.currentQuetionIndex + 1};
    }
    return state;                                   // definng reducer function - to update state
}


const Quiz = () => {

const [state, dispatch] = useReducer(reducer, initialState);        //using reducer
const testClick = () => {
    console.log("testclick");
    dispatch({type: "NEXT_QUESTION"});
}

    return (
        <div className="quiz">
            <div>
                <div className="score">Question 1/8</div>
                <Questions />
                <div className="next-button" onClick = {testClick}>Next question{currentQuetionIndex} </div>
            </div>
 
        </div>
    );
}

export default Quiz;
