import { useReducer, createContext } from "react";
import questions from "../data";
import { shuffleAnswers } from "../helpers";

const initialState = {
    currentQuestionIndex: 0,
    questions,
    showResults: false,
    answers: shuffleAnswers(questions[0]),
    currentAnswer: '',
    currentAnswerCount: 0
}

const reducer = (state, action) => {
    console.log('reducer', state, action);
    switch (action.type) {
        case 'SELECT_ANSWER':
            {   const correctAnswerCount = state.questions[state.currentQuestionIndex].correctAnser ? correctAnswerCount + 1 : correctAnswer;
                return {...state, currentAnswer: action.payload};
            }
        case 'NEXT_QUESTION':
            {
                const showResults = state.currentQuestionIndex === state.questions.length - 1;
                const currentQuestionIndex = showResults ? state.currentQuestionIndex : state.currentQuestionIndex + 1;
                const answers = showResults ? [] : shuffleAnswers(state.questions[currentQuestionIndex]);
                return { ...state, currentQuestionIndex, showResults, answers, currentAnswer: "" };
            }

        case 'RESTART':
            {
                return initialState;
            }

        default:
            {
                return state;
            }
    }

}


export const QuizContext = createContext();

export const QuizProvider = ({ children }) => {
    const value = useReducer(reducer, initialState);
    console.log("state", value);

    return <QuizContext.Provider value={value} >{children}</QuizContext.Provider>   //provdier is the property of context
}
